import { createRouter, createWebHashHistory } from 'vue-router';
import CreateAdvert from './components/createAdvert.vue';
import AdvertView from './components/advertView.vue';
import HomeView from './components/homeView.vue';
import CvUpload from './components/CvUpload.vue';
import Login from './components/Login.vue';
import AdminView from './components/AdminAndComponents/AdminView.vue';
import UserList from './components/AdminAndComponents/UserList.vue';


const router = createRouter({
  history: createWebHashHistory(),
  routes: [
    {path:"/", name:Login,component :Login},
    { path: '/createAdvert', component: CreateAdvert },
    {
      path: '/edit/:id',
      name: 'EditAdvert',
      component: CreateAdvert,
      // Route parametrelerini prop olarak geçiyoruz ve editMode'u true yapıyoruz
      props: route => ({ id: Number(route.params.id), editMode: true })
    },
    { path: `/adverts`, component: AdvertView },
    { path: '/home', component: HomeView },
    {path:'/CvUpload',component: CvUpload},
    
    {
      path: "/admin", component: AdminView, children: [
        { path: "users", component: UserList },
        { path: "adverts", component: UserList },
        { path: "cvs", component: UserList },
        
      ]
    }

  ]
});

export default router;
