import pageViewer from './components/pageViewer.vue';
import Navbar from './components/Navbar.vue';
export default {
  components: {
    pageViewer,
    Navbar
  },
  data() {
    return {
      activePage: 0,
      pages: []
    };
  },
  beforeCreate() {
    this.getPages();
  },
  created() {
    this.getPages();
  },
  methods: {
    async getPages() {
      let res = await fetch('pages.json');
      let data = await res.json();
      this.pages = data;
    }
  }
};
