import "core-js/modules/es.array.push.js";
import Navbar from './components/Navbar.vue';
export default {
  components: {
    Navbar
  },
  data() {
    return {
      showNavbar: false,
      user: null,
      userInfo: {
        id: null,
        type: ''
      }
    };
  },
  created() {
    // Sayfa yenilendiğinde showNavbar ve user bilgilerini localStorage'dan yükle
    const activeUserId = localStorage.getItem("ActiveUserId");
    const activeUserType = localStorage.getItem("ActiveUserType");
    if (activeUserId && activeUserId !== '-1') {
      this.user = {
        id: activeUserId,
        type: activeUserType
      };
      this.showNavbar = true; // Eğer geçerli bir kullanıcı varsa navbar'ı göster
    }
  },
  methods: {
    handleLoginSuccess(user) {
      this.user = user;
      localStorage.setItem("ActiveUserId", user.id);
      localStorage.setItem("ActiveUserType", user.type);
      this.showNavbar = true;
      this.$router.push('/home');
    },
    hanldeExit() {
      localStorage.setItem("ActiveUserId", -1);
      localStorage.setItem("ActiveUserType", "");
      this.showNavbar = false;
    }
  }
};
