import Vue from 'vue';
import Router from 'vue-router';
import createAdvert from '@/components/createAdvert.vue';
import advertView from '@/components/advertView.vue';

Vue.use(Router);

export default new Router({
  routes: [
    {
      path: '/',
      redirect: '/adverts'
    },
    {
      path: '/adverts',
      name: 'advertView',
      component: advertView
    },
    {
      path: '/create',
      name: 'createAdvert',
      component: createAdvert
    },
    {
      path: '/edit/:index',
      name: 'editAdvert',
      component: createAdvert,
      props: true
    }
  ]
});
