import { createRouter, createWebHashHistory } from 'vue-router';
import CreateAdvert from './components/createAdvert.vue';
import AdvertView from './components/advertView.vue';
import HomeView from './components/homeView.vue';

const routes = [
  { path: '/createAdvert', component: CreateAdvert },
  { path: '/adverts', component: AdvertView },
  { path: '/adverts/edit/:index', component: CreateAdvert, props: true },
  { path: '/home', component: HomeView }
];

const router = createRouter({
  history: createWebHashHistory(),
  routes
});

export default router;
