import { createStore } from 'vuex';

export default createStore({
  state: {
    activeUserId: 0,
    activeUserType: '',
    showNavBar: true,
  },
  mutations: {
    setActiveUser(state, user) {
      state.activeUserId = user.id;
      state.activeUserType = user.type;
    },
    setShowNavBar(state, value) {
      state.showNavBar = value;
    },
    clearActiveUser(state) {
      state.activeUserId = 0;
      state.activeUserType = '';
      state.showNavBar = false;
    }
  },
  actions: {
    updateActiveUser({ commit }, user) {
      commit('setActiveUser', user);
    },
    updateShowNavBar({ commit }, value) {
      commit('setShowNavBar', value);
    },
    logout({ commit }) {
      commit('clearActiveUser');
    }
  },
  getters: {
    getActiveUserId: state => state.activeUserId,
    getActiveUserType: state => state.activeUserType,
    getShowNavBar: state => state.showNavBar,
  }
});
