const advertKey='advert';
const pagesKey='pages';


let advertJson=localStorage.getItem(advertKey);
let advertStore=JSON.parse(advertJson);

let pageJson=localStorage.getItem(pagesKey);
let pageStore=JSON.parse(pageJson);


export default{
    
    getAllAdvert(){
        return advertStore;
    },
    getSingleAdvert(index){
        return advertStore[index];
    },
    getAllPages(){
        return pageStore;
    },
    getSinglePage(index){
        return pageStore[index];
    }
    
}
