const advertKey = 'advert';
const pagesKey = 'pages';

function loadFromLocalStorage(key) {
  const json = localStorage.getItem(key);
  return JSON.parse(json) || [];
}

function saveToLocalStorage(key, data) {
  localStorage.setItem(key, JSON.stringify(data));
}

let advertStore = loadFromLocalStorage(advertKey);
let pageStore = loadFromLocalStorage(pagesKey);

export default {
  getAllAdvert() {
    return advertStore;
  },
  getSingleAdvert(index) {
    return advertStore[index];
  },
  getAllPages() {
    return pageStore;
  },
  getSinglePage(index) {
    return pageStore[index];
  },
  addAdvert(advert) {
    advertStore.push(advert);
    saveToLocalStorage(advertKey, advertStore);
  },
  updateAdvert(index, advert) {
    advertStore[index] = advert;
    saveToLocalStorage(advertKey, advertStore);
  },
  deleteAdvert(index) {
    advertStore.splice(index, 1);
    saveToLocalStorage(advertKey, advertStore);
  },
  addPage(page) {
    pageStore.push(page);
    saveToLocalStorage(pagesKey, pageStore);
  },
  updatePage(index, page) {
    pageStore[index] = page;
    saveToLocalStorage(pagesKey, pageStore);
  },
  deletePage(index) {
    pageStore.splice(index, 1);
    saveToLocalStorage(pagesKey, pageStore);
  }
};
